  )

  days = [5,7].sample

  open_hours = ["0500", "0600", "0700"].sample
  close_hours = ["1600", "1800", "2200"].sample
  days.times do |day|
    day_of_week = "Mon" if day == 0
    day_of_week = "Tues" if day == 1
    day_of_week = "Wed" if day == 2
    day_of_week = "Thurs" if day == 3
    day_of_week = "Fri" if day == 4
    day_of_week = "Sat" if day == 5
    day_of_week = "Sun" if day == 6
    StoreHour.create!(
      store_id: s_id+1,
      day_of_week: day_of_week,
      time_open: open_hours,
      time_closed: close_hours,
    )
  end
end


